function updateHighlightFaceId() {
    let faceid = -1;
    const face = getFace([
        cameraPos[0],
        cameraPos[1],
        cameraPos[2]
    ], [
        leftFaceInfo,
        rightFaceInfo,
        topFaceInfo,
        backFaceInfo,
        frontFaceInfo,
        bottomFaceInfo
    ]);
    if (face) {
        const crossPt = calcPlaneLineCrossPoint([
            cameraPos[0],
            cameraPos[1],
            cameraPos[2]
        ], [
            cameraPos[0],
            cameraPos[1],
            cameraPos[2]
        ], face.pointOnPlane, face.normal);
        if (crossPt) {
            const faceIndex = getFaceId(crossPt, face);
            const faceId = face.faceIdAry[faceIndex];
            faceid = faceId;
        }
    }
    return faceid;
}
